sys_variable_value.value:
(function execute(inputs, outputs) {

  var responseBody = inputs.responseBody;
  var statusCode = inputs.statusCode;
  var content = '>>No content in ChatGPT response, please review log for errors<<';

  if(statusCode == 200 || statusCode == 201 || statusCode == 204){
    if(responseBody){
      responseBody = JSON.parse(responseBody)
      if(responseBody.hasOwnProperty('text')){
          content = responseBody.text;
      } else {
          gs.error('No content in ChatGPT response for prompt: ' + inputs.prompt.number + '. The response body is: ' + responseBody);
      }
    }

    outputs.content = content;
  }
})(inputs, outputs);

