sys_variable_value.value:
(function execute(inputs, outputs) {

  var model = inputs.model;
  var prompt = inputs.prompt;

  var messages = [{"role": "user", "content": prompt}];

  var payload = {
  	"model": model,
    "messages": messages
  }

  outputs.payload = payload;

})(inputs, outputs);

