sys_variable_value.value:
(function execute(inputs, outputs) {

  var model = inputs.model;
  var promptGR = inputs.promptGR;
  var conversation = promptGR.getValue('conversation');
  var messages = [];

  // No conversation
  if(conversation == ""){
  	messages.push({"role": "user", "content": promptGR.getValue('prompt')});

  // Conversation
  } else {

    // Retrieve all prior prompts and responses to add to messages object
    var grPrompts = new GlideRecord('x_648117_chatgpt_base');
    grPrompts.addQuery('conversation',conversation);
    grPrompts.addQuery('state', 'complete');
    grPrompts.orderBy('sys_created_on');
    grPrompts.query();
    while(grPrompts.next()){
      var prompt = {"role": "user", "content": grPrompts.getValue('prompt')};
      var response = {"role": "assistant", "content": grPrompts.getValue('response')};
      messages.push(prompt);
      messages.push(response);
    }

    // Add current prompt to messages object
    messages.push({"role": "user", "content": promptGR.getValue('prompt')});
  }

  var payload = {
  	"model": model,
    "messages": messages
  }

  outputs.payload = payload;
})(inputs, outputs);

