sys_variable_value.value:
(function execute(inputs, outputs) {

  // Retrieve inputs
  var system_user_text = inputs.system_user_text;
  var input_text = inputs.input_text;
  var conversation = inputs.conversation;
  var payload = JSON.parse(inputs.payload); // Parse payload into an object as it comes into the step as a string

  // Initialize messages object
  var messages = [];

  // No conversation
  if(global.JSUtil.nil(conversation)){
  	messages.push({"role": "user", "content": input_text});

  // Conversation
  } else {

    // Retrieve all prior prompts and responses to add to messages object
    var grPrompts = new GlideRecord('x_648117_chatgpt_base');
    grPrompts.addQuery('conversation',conversation);
    grPrompts.addQuery('state', 'complete');
    grPrompts.orderByDesc('sys_created_on');
    grPrompts.setLimit(gs.getProperty('x_648117_chatgpt.max_number_of_prompts'));
    grPrompts.query();
    while(grPrompts.next()){
      var prompt = {"role": "user", "content": grPrompts.getValue('prompt')};
      var response = {"role": "assistant", "content": grPrompts.getValue('response')};
      messages.unshift(response); // Unshift to move to front of array in order of prompt - response
      messages.unshift(prompt); // Unshift to move to front of array in order of prompt - response
    }

    // Add current prompt to end of messages object
    messages.push({"role": "user", "content": input_text});
  }

  // If persona populated set to front of array
  if (system_user_text){
  	messages.unshift({"role": "system", "content": system_user_text});
  }

  // Append messages to the payload
  payload.messages = messages;

  // Translate floating point numbers from strings into number as payload builder stringifies everything
  payload.temperature = parseFloat(payload.temperature);
  payload.presence_penalty = parseFloat(payload.presence_penalty);
  payload.frequency_penalty = parseFloat(payload.frequency_penalty);

  // Output payload
  outputs.payload = payload;
})(inputs, outputs);

