sys_variable_value.value:
(function execute(inputs, outputs) {

  var responseBody = inputs.responseBody;
  var statusCode = inputs.statusCode;
  var content = '>>No content in ChatGPT response, please review log for errors<<';

  if(statusCode == 200 || statusCode == 201 || statusCode == 204){
    if(responseBody){
      responseBody = JSON.parse(responseBody)
      if(responseBody.choices[0].message.content){
          content = responseBody.choices[0].message.content;
      } else {
          gs.error('No content in ChatGPT response for prompt: ' + inputs.prompt.number + '. The response body is: ' + responseBody);
      }

      if(responseBody.usage){
        outputs.prompt_tokens = responseBody.usage.prompt_tokens;
        outputs.completion_tokens = responseBody.usage.completion_tokens;
        outputs.total_tokens = responseBody.usage.total_tokens;
      } else {
          gs.error('No usage in ChatGPT response for input: ' + inputs.input_text + '. The response body is: ' + responseBody);
      }
    }

    outputs.content = content;
  }
})(inputs, outputs);

